    private void btnIdenticalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIdenticalActionPerformed
        // TODO add your handling code here:
        String value1 = txtIdentical1.getText();
        String value2 = txtIdentical2.getText();
        boolean flag = value1.equals(value2);
        lblIdenticalValue.setText(String.valueOf(flag));

    }//GEN-LAST:event_btnIdenticalActionPerformed

    private void btnFoundActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnFoundActionPerformed
        // TODO add your handling code here:
        String value1 = txtFound.getText();
        String value2 = txtFoundLetter.getText();
        int num = value1.indexOf(value2);
        lblFoundValue.setText(String.valueOf(num));
    }//GEN-LAST:event_btnFoundActionPerformed

