    private void btnLengthActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLengthActionPerformed
        // TODO add your handling code here:
        String value = txtLength.getText();
        value =   value.trim();
        int length = value.length();
        lblLengthValue.setText(String.valueOf(length));
        

       
      
        
      

    }//GEN-LAST:event_btnLengthActionPerformed

    private void btnCapsActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCapsActionPerformed
        // TODO add your handling code here:
        String value = txtCaps.getText();
        value = value.toUpperCase();
        lblCapsValue.setText(value);
    }//GEN-LAST:event_btnCapsActionPerformed

    private void btn4thActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn4thActionPerformed
        // TODO add your handling code here:
        String value = txt4th.getText();
        char C = value.charAt(4);
        lbl4thValue.setText(String.valueOf(C));
    }//GEN-LAST:event_btn4thActionPerformed

    private void btn4ToActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn4ToActionPerformed
        // TODO add your handling code here:
        String value = txt4To6.getText();
        value = value.substring(4, 7); 
        lbl4To6Value.setText(value);
    }//GEN-LAST:event_btn4ToActionPerformed

    private void btnReplaceActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnReplaceActionPerformed
        // TODO add your handling code here:
        String value = txtReplace.getText();
        value = value.replace("a","x");
        lblReplaceValue.setText(value);
    }//GEN-LAST:event_btnReplaceActionPerformed

